<style lang="scss">
    .lockup {
        --duration: 0.75s;
        --delay: 1s;

        .background-layer {
            opacity: 0;
            animation: fade-in var(--duration) ease-in-out forwards 2.75s;
        }

        @keyframes grid-lines {
            from {
                transform: translate3d(49px, 88px, 0px);
            }
            to {
                opacity: 1;
                transform: translate3d(0px, 0px, 0px);
            }
        }

        .grid-lines > path {
            opacity: 0;
            animation: grid-lines var(--duration) ease-in-out forwards var(--delay);
        }

        @keyframes top-layer {
            from {
                transform: translate3d(51px, 88px, 10px);
            }
            to {
                transform: translate3d(0px, 0px, 0px);
                opacity: 1;
            }
        }

        .top-layer {
            opacity: 0;
            animation: top-layer var(--duration) ease-in-out forwards var(--delay);
        }

        @keyframes middle-layer {
            from {
                transform: translate3d(25px, 43px, 0px);
            }
            to {
                transform: translate3d(0px, 0px, 0px);
                opacity: 1;
            }
        }

        .middle-layer {
            opacity: 0;
            animation: middle-layer var(--duration) ease-in-out forwards var(--delay);
        }
    }
</style>
